// Home Page Component
const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const recentMoviesRef = useRef(null);

  useEffect(() => {
    const fetchMovies = async () => {
      const recentMovies = await fetchRecentMovies();
      setMovies(recentMovies);
    };

    fetchMovies();
  }, []);

  const handleMovieClick = (movie) => {
    navigate(`/movies/${movie.title.replace(/\s+/g, '-').toLowerCase()}/theatre-details`);
  };

  const handleSearchChange = (event) => {
    setSearch(event.target.value);
  };

  const handleExploreNowClick = () => {
    recentMoviesRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const filteredMovies = movies.filter((movie) =>
    movie.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container>
      <HeroSection>
        <HeroTitle
          initial={{ y: -100, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          Discover the Best Movies
        </HeroTitle>
        <HeroButton
          initial={{ scale: 1 }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
          onClick={handleExploreNowClick}
        >
          Explore Now
        </HeroButton>
      </HeroSection>

      <Header>
        <Logo
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
          onClick={() => navigate('/')}
        >
          MovieVerse
        </Logo>
        <Nav>
          <NavLink
            to="/about"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
          >
            About
          </NavLink>
          <NavLink
            to="/contact"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
          >
            Contact
          </NavLink>
          <NavLink
            to="/top-rated"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
          >
          </NavLink>
        </Nav>
        <AuthBar>
          <AuthLink
            to="/login"
            whileHover={{ scale: 1.05 }}
          >
            Login
          </AuthLink>
          <AuthLink
            to="/signup"
            whileHover={{ scale: 1.05 }}
          >
            Sign Up
          </AuthLink>
        </AuthBar>
      </Header>

      <SearchBar
        type="text"
        placeholder="Search for a movie..."
        value={search}
        onChange={handleSearchChange}
      />

      <SectionTitle
        ref={recentMoviesRef}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        Recent Movies
      </SectionTitle>

      <MoviesSection
        initial="hidden"
        animate="visible"
        variants={{
          hidden: {
            opacity: 0,
            y: 50,
          },
          visible: {
            opacity: 1,
            y: 0,
            transition: {
              delayChildren: 0.3,
              staggerChildren: 0.2,
            },
          },
        }}
      >
        {filteredMovies.map((movie, index) => (
          <MovieCard
            key={index}
            onClick={() => handleMovieClick(movie)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <MovieImage
              src={movie.image || 'https://via.placeholder.com/300x450?text=No+Image'}
              alt={movie.title}
            />
            <MovieTitle>{movie.title}</MovieTitle>
          </MovieCard>
        ))}
      </MoviesSection>

      <Footer>&copy; 2024 MovieVerse. All rights reserved.</Footer>
    </Container>
  );
};

export default HomePage;